mapper.select:
SELECT * FROM( SELECT ROWNUM rn, a.* FROM( SELECT c.* , u.user_name, u.user_addr1 , co.CONTENT, img.img_url ,substr(manu_date,1,2) || '년' || substr(manu_date,3,4) || '월식' munu_date_cos FROM car_general_info c JOIN user_table u ON c.USER_ID = u.USER_ID JOIN COMMON co ON c.BRAND = co.BRAND LEFT JOIN CAR_IMAGES img ON c.sell_num = img.sell_num WHERE ( c.model LIKE '%' || #{keyword} || '%' or c.COLOR LIKE '%' || #{keyword} || '%' or c.TRANSMISSION LIKE '%' || #{keyword} || '%' or c.CAR_NUM LIKE '%' || #{keyword} || '%' or u.user_addr1 LIKE '%' || #{keyword} || '%' or u.user_name LIKE '%' || #{keyword} || '%' or co.CONTENT LIKE '%' || #{keyword} || '%' ) AND c.SALE = 0 AND c.del_state = 0 AND u.del_state = 0
AND c.car_type = #{searchType}
AND ( img.IMG_URL LIKE '%' || '_1.' || '%' OR img.IMG_URL IS NULL ) ORDER BY c.REG_DATE desc )a ) WHERE rn BETWEEN #{start} and #{end}

		SELECT COUNT(*)
		FROM car_general_info c
		JOIN user_table u 
        ON c.USER_ID = u.USER_ID
		WHERE BRAND = #{brand}
		AND FUEL LIKE #{fuel}
		AND TO_NUMBER(SUBSTR(manu_date, 1, 2)) BETWEEN #{manu_date1} AND #{manu_date2}
		AND c.SALE = 0
        AND c.del_state = 0
        AND u.del_state = 0

